import axios from 'axios';
import * as cheerio from 'cheerio';
export async function pinterest(text) {
    try {
        const response = await axios.get(`https://id.pinterest.com/search/pins/?autologin=true&q=${encodeURIComponent(text)}`, {
            headers: {
                "cookie": "_auth=1; _b=\"AXOtdcLOEbxD+qMFO7SaKFUCRcmtAznLCZY9V3z9tcTqWH7bPo637K4f9xlJCfn3rl4=\"; _pinterest_; _ir=0"
            }
        });
        const data = response.data;
        const $ = cheerio.load(data);
        const result = [];
        const images = [];
        $('div > a').each((i, element) => {
            const link = $(element).find('img').attr('src');
            if (link) {
                result.push(link);
            }
        });
        result.forEach(v => {
            if (v && v.includes('236')) {
                images.push(v.replace(/236/g, '736'));
            }
        });
        images.shift();
        return images;
    } catch (error) {
        throw error;
    }
}